  <template>
    <div>
      <HeaderPage /> 
      <h1 style="text-align: center;">Resources</h1>   
  
      <div class="button1">
        <button @click="showFrontend">Front-End</button>
        <button @click="showBackend">Back-End</button>
        <button @click="showDatabase">DataBase</button>
        <button @click="showDevops">DevOps</button>
        <button @click="showAll">All</button>
      </div>
  
      <div class="page-content">
        <div v-for="resource in filteredResources" 
          :key="resource.id" 
          @click="navigateToResource(resource.link)" 
          class="resource-card">
          <img :src="resource.image" alt="Resource Image" class="resource-image">
          <div class="resource-info">
            <h2>{{ resource.label }}</h2>
            <p>{{ resource.description }}</p>
          </div>
        </div>
      </div>
    </div>
  </template>
  
  <script>
  import HeaderPage from "@/components/Header/HeaderPage.vue";
  
  export default {
    name: 'ResourcePage',
    components: {
      HeaderPage,
    },
    data() {
      return {
        resources: [
        {
          id: 1,
          label: 'Docker',
          description: 'Hello, Docker',
          link: 'Docker',
          category: 'DevOps',
          image: 'data:image/png;base64,',
        },
        {
          id: 2,
          label: 'Podman',
          description: 'Hello, Podman',
          link: 'Podman',
          category: 'DevOps',
          image: 'data:image/png;base64,',
        },
        {
          id: 3,
          label: 'HTML',
          description: 'Hello, HTML',
          link: 'HTML',
          category: 'Front-End',
          image: 'data:image/png;base64,',
        },
        {
          id: 4,
          label: 'CSS',
          description: 'Hello, CSS',
          link: 'CSS',
          category: 'Front-End',
          image: 'data:image/png;base64,',
        },
        {
          id: 5,
          label: 'NodeJs',
          description: 'Hello, NodeJs',
          link: 'NodeJs',
          category: 'Back-End',
          image: 'data:image/png;base64,',
        },
        {
          id: 6,
          label: 'MySql',
          description: 'Hello, MySql',
          link: 'MySql',
          category: 'DataBase',
          image: 'data:image/png;base64,',
        },
        {
          id: 7,
          label: 'PostgreSql',
          description: 'Hello, PostgreSql',
          link: 'PostgreSql',
          category: 'DataBase',
          image: 'data:image/png;base64,',
        },
        {
          id: 8,
          label: 'MongoDB',
          description: 'Hello, MongoDB',
          link: 'MongoDB',
          category: 'DataBase',
          image: 'data:image/png;base64,',
        },
        {
          id: 9,
          label: 'JavaScript',
          description: 'Hello, JavaScript',
          link: 'JavaScript',
          category: 'Front-End',
          image: 'data:image/jpeg;base64,',
        }
        ],
        selectedCategory: 'All', // Default category is All
      };
    },
    computed: {
      filteredResources() {
        if (this.selectedCategory === 'All') {
          return this.resources;
        } else {
          return this.resources.filter(resource => resource.category === this.selectedCategory);
        }
      }
    },
    methods: {
      navigateToResource(resourcelink) {
        this.$router.push({ path: `/${resourcelink}` });
      },
      showFrontend() {
        this.selectedCategory = 'Front-End';
      },
      showBackend() {
        this.selectedCategory = 'Back-End';
      },
      showDatabase() {
        this.selectedCategory = 'DataBase';
      },
      showDevops() {
        this.selectedCategory = 'DevOps';
      },
      showAll() {
        this.selectedCategory = 'All';
      },
    },
  };
  </script>
  
  
  <style scoped>

  .page-content {
    padding: 20px;
    display: flex;
    flex-wrap: wrap;
    overflow-y: scroll;
  }
  
  .resource-card {
    border: 1px solid #ccc;
    padding: 10px;
    width: 300px;
    /* display: flex; */
    text-align: center;
    margin: 10px;
    cursor: pointer;
    transition: transform 0.2s ease-in-out;
  }

  .resource-card:hover {
    transform: scale(1.05);
  }
  .resource-image {
  width: 150px; /* Set the desired width */
  height: 100px; /* Set the desired height */
  object-fit: cover;
  margin-right: 15px;
  mix-blend-mode: color-burn;
}

.resource-info {
  flex-grow: 1; /* Allow the info to grow and take the available space */
}

.button1{
margin: 5px;
text-align: center;
}

button {
  margin: 5px;
  font-size: 18px;
  border-radius: 5px;
  background-color: bisque;
}
  
